import React, { useRef, useEffect } from "react";

const Player = ({ currentTrack, isPlaying, volume }) => {
  const audioRef = useRef();

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.volume = volume;

      if (isPlaying) {
        audioRef.current.play().catch((err) => {
          console.error("Playback error:", err);
        });
      } else {
        audioRef.current.pause();
      }
    }
  }, [currentTrack, isPlaying, volume]);

  return (
    <div className="p-6 bg-white rounded-xl shadow-md w-full max-w-md text-center">
      <h2 className="text-xl font-semibold mb-4">{currentTrack.title}</h2>
      <audio ref={audioRef} src={currentTrack.url} />
      <p className="text-sm text-gray-600">Volume: {Math.round(volume * 100)}%</p>
    </div>
  );
};

export default Player;
